INSERT INTO `products` (`id`, `name`, `description`, `price`, `date`, `category`, `image`, `identity`, `meta_title`, `meta_description`, `meta_keywords`) VALUES
(57, 'Palm Angels плащ оверсайз с логотипом', 'Плащ оверсайз с логотипом от Palm Angels. Классический воротник, застежка на кнопках, шлица сзади, длинные рукава, свободный крой и удлиненная модель. Цвет: белый.', 50900.00, '0000-00-00 00:00:00', 2, '14929614-24536766-1000.jpg.webp', 'palm-angels-oversize-coat-with-logo', 'Palm Angels плащ оверсайз с логотипом', 'Palm Angels плащ оверсайз с логотипом', 'Palm Angels плащ оверсайз с логотипом'),
(58, 'Heron Preston ветровка с нашивкой', 'Ветровка с нашивкой \'Стиль\' от Heron Preston. Легкая конструкция, контрастный воротник , фирменная заплатка в форме звезды, декоративная строчка, застежка на пуговицах поверх молнии, передние карманы, шлица сзади и штормовая заслонка. Цвет: бежевый, оранжевый.', 39158.00, '0000-00-00 00:00:00', 2, '14100337_21666081_1000.jpg', 'heron-preston-jacket-with-stripe', 'Heron Preston ветровка с нашивкой', 'Heron Preston ветровка с нашивкой', 'Heron Preston ветровка с нашивкой'),
(59, 'Burberry пальто с принтом Horseferry', 'Пальто с принтом Horseferry от Burberry (Берберри). Классический воротник, застежка на пуговицах спереди, принт с логотипом спереди, узор сзади, длинные рукава, полоски сбоку, узкие манжеты, средней длины и шлица сзади. Цвет: желтый.', 209000.00, '0000-00-00 00:00:00', 2, '14882436_24360970_1000.jpg.webp', 'burberry-coat-with-horseferry-print', 'Burberry пальто с принтом Horseferry', 'Burberry пальто с принтом Horseferry', 'Burberry пальто с принтом Horseferry'),
(60, 'Z Zegna фактурное двубортное пальто', 'Фактурное двубортное пальто от Z Zegna. Средней длины, застежка на пуговицы и два передних кармана. Цвет: черный.', 46897.00, '0000-00-00 00:00:00', 2, '14435729_22605895_1000.jpg.webp', 'z-zegna-breasted-coat', 'Z Zegna фактурное двубортное пальто', 'Z Zegna фактурное двубортное пальто', 'Z Zegna фактурное двубортное пальто'),
(61, 'Junya Watanabe MAN двубортное пальто с контрастными рукавами', 'Двубортное пальто с контрастными рукавами от Junya Watanabe MAN. Зазубренные лацканы, застежка на пуговицах спереди, передние карманы с клапанами, нагрудный карман, шлица сзади, длинные рукава и декоративная строчка. Цвет: черный.', 60053.00, '0000-00-00 00:00:00', 2, '14447649_21832492_1000.jpg.webp', 'junya-watanabe-breasted-coat', 'Junya Watanabe MAN двубортное пальто с контрастными рукавами', 'Junya Watanabe MAN двубортное пальто с контрастными рукавами', 'Junya Watanabe MAN двубортное пальто с контрастными рукавами'),
(62, 'Gucci пуховик с узором GG Supreme', 'Если вы подбираете себе повседневный образ, почему бы не придать ему нотку элегантности? В таком случае Gucci будет вашим лучшим выбором. Этот серый пуховик с узором GG Supreme и белыми полосками по бокам от Gucci (Гуччи) имеет застежку на пуговицах спереди, капюшон, длинные рукава, боковые карманы и подол на шнурке. Сделано в Италии.', 151460.00, '0000-00-00 00:00:00', 2, '14185435-21328123-1000.jpg', 'gucci-jacket-with-gg-supreme-pattern', 'Gucci пуховик с узором GG Supreme', 'Gucci пуховик с узором GG Supreme', 'Gucci пуховик с узором GG Supreme'),
(63, 'Rick Owens пальто Slab с капюшоном', 'Пальто Slab с капюшоном от Rick Owens (Рик Оуэнс). С капюшоном, смещенная застежка на молнии, прорезные карманы по бокам, карманы с клапанами сбоку, закругленный подол, разрез на кайме сзади и декоративная окантовка. Цвет: серый.', 114750.00, '0000-00-00 00:00:00', 2, '13721697_16626186_1000.jpg', 'rick-owen-coat-slab', 'Rick Owens пальто Slab с капюшоном', 'Rick Owens пальто Slab с капюшоном', 'Rick Owens пальто Slab с капюшоном'),
(64, 'Martine Rose дутая парка Wenger', 'Дутая парка Wenger от Martine Rose. Капюшон, воротник с высокой стойкой, потайная застежка спереди, длинные рукава, средней длины и заплатка с логотипом спереди. Цвет: черный.', 42777.00, '0000-00-00 00:00:00', 2, '13783253_21368116_1000.jpg.webp', 'martine-rose-wenger-parka', 'Martine Rose дутая парка Wenger', 'Martine Rose дутая парка Wenger', 'Martine Rose дутая парка Wenger'),
